      this.safeClearTimeout();
      this.forceReject && this.forceReject(new IntendedError());
      this.setState({ ...initialState }, () => this.start());
    }
  }

  componentWillUnmount() {
    this._isMounted = false;
    this.forceReject = undefined;
    this.safeClearTimeout();
  }

