    const img = this.imgRef.current;

    if (!img) {
      return;
    }

    const { onLoad } = this.props;
    return new Promise((resolve, reject) => {
      this.forceReject = reject;

      const onResolve = async () => {
        if (img.decode !== undefined) {
          try {
            await img.decode();
