import React from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';

import './styles.css';

const DEFAULT_DURATION_MS = 1600;
const DEFAULT_HEIGHT = 400;
const DEFAULT_WIDTH = 400;

export interface ShimmerProps {
  height: number;
  width: number;
  className?: string;
  duration?: number;
}

const calcShimmerStyle = (
  width: number,
  height: number,
  duration = DEFAULT_DURATION_MS
) => ({
  backgroundSize: `${width * 10}px ${height}px`,
  animationDuration: `${(duration / 1000).toFixed(1)}s`,
});

export const Shimmer = ({
  className,
  duration,
  height = DEFAULT_HEIGHT,
  width = DEFAULT_WIDTH,
}: ShimmerProps) => {
  const shimmerStyle = calcShimmerStyle(width, height, duration);
  const style = { ...shimmerStyle, ...{ height, width } };

  return <div className={clsx('shimmer', className)} style={style} />;
};

Shimmer.propTypes = {
  height: PropTypes.number.isRequired,
  width: PropTypes.number.isRequired,
  className: PropTypes.string,
  duration: PropTypes.number,
};
import React from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';

import './styles.css';

export interface BreathingProps {
  className?: string;
  duration?: number;
  height?: number;
  width?: number;
}

const DEFAULT_DURATION_MS = 1000;

export const Breathing = ({
  className,
  duration = DEFAULT_DURATION_MS,
  height,
  width,
}: BreathingProps) => {
  const style = {
    height,
    width,
    animationDuration: `${(duration / 1000).toFixed(1)}s`,
  };
  return <div className={clsx('breathing', className)} style={style} />;
};

Breathing.propTypes = {
  className: PropTypes.string,
  duration: PropTypes.number,
  height: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
  width: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
};
