  const crxcoin = [...coinList].filter((a) => a.coin === "crx");
  const coinlist = [...coinList].filter((a) => a.coin !== "crx");
  const coinlistfull = crxcoin.concat(coinlist);

  // const listofcoins = crxlist.concat[coinlist];
